          component: NewInvestigation
        },
        {
          path: 'investigations/:id',
          name: 'EditInvestigation',
          component: EditInvestigation
        }
      ]
    },
    {
      path: '/login/',
      name: 'Login',
      component: Login
    },
    {
      path: '*',
      name: 'PageNotFound',
      component: PageNotFound
    }
  ]
})



